import styles from "./SubmitOrder.module.css";
import useInput from "../../hooks/useInput";

const SubmitOrder = (props) => {

  // Name
  const {
    value: enteredName,
    hasError: hasNameInputError,
    isValid: isNameInputValid,
    inputChangeHandler: nameInputChangeHandler,
    inputLostFocusHandler: nameInputLostFocusHandler,
    resetValues: resetNameInputValue,
  } = useInput((value) => value.trim() !== "");

  const confirmOrderHandler = (event) => {
    event.preventDefault();

    if(!isNameInputValid){
        return
    }

    console.log(enteredName);

    resetNameInputValue()
  };

  let isFormValid = false

  if(isNameInputValid) {
    isFormValid = true
  }

  const nameInputClass = hasNameInputError ? "styles.invalid" : "styles.control"



  return (
    <form onSubmit={confirmOrderHandler} className={styles.form}>
      <div className={styles.control}>
        <label htmlFor="name">Enter your name</label>
        <input
          id="name"
          type="text"
          value={enteredName}
          onChange={nameInputChangeHandler}
          onBlur={nameInputLostFocusHandler}
        />
        {hasNameInputError && <p>Enter your name</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="city">Enter your city</label>
        <input id="city" type="text" />
        {<p>Enter your city</p>}
      </div>
      <div className={styles.control}>
        <label htmlFor="adress">Enter your adress</label>
        <input id="adress" type="text" />
        {<p>Enter your adress</p>}
      </div>
      <div className={styles.actions}>
        <button className={styles.submit} disabled={!isFormValid}>Order</button>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
      </div>
    </form>
  );
};

export default SubmitOrder;
